(function($) {

	// Init Popups
	SUPER.init_popups = function() {

		$('.super-popup-wrapper > .super-popup').each(function(){
			var $this = $(this);
			var $settings = $this.children('textarea[name="super-popup-settings"]').val();
			$settings = jQuery.parseJSON($settings);

			// Display the Close button (X) on top right corner of the popup
			if( ($settings.close_button)  == 'true' ) {
				alert('test1');
				var $close_btn = $this.children('.super-popup-close');
				$close_btn.css('background-color', $settings.close_button_bg_color);
				$close_btn.css('color', $settings.close_button_icon_color);
				$close_btn.show();
			}else{
				$this.children('.super-popup-close').hide();
			}

			// Display on page load
			if( ($settings.page_load) == 'true' ) {
				 SUPER.init_popups.show( $this, $settings );
			}

			// Exit intent
			if( ($settings.exit_intent)  == 'true' ) {
				jQuery(document).bind('mouseleave', function(e) {
					SUPER.init_popups.show( $this, $settings );
				});
			}

			// Display the popup on page leave / page close/exit
			if( ($settings.leave)  == 'true' ) {
				jQuery(document).bind('mouseleave', function(e) {
					SUPER.init_popups.show( $this, $settings );
				});
				window.onbeforeunload = function(e) {
					SUPER.init_popups.show( $this, $settings );
				    var $msg = $settings.leave_msg;
					(e || window.event).returnValue = $msg;
				    return $msg;
				}
			}

			// Display the popup when user scrolled xx% of the page
			if( parseInt($settings.scrolled) > 0 ) {
				var page_height = $(document).height() - $(window).height();  
				$( window ).scroll(function() {    
					var scroll_level = $(window).scrollTop();  
					var scroll_level_percentage = ( parseInt(scroll_level) * 100 ) / page_height; 
					if( parseInt(scroll_level_percentage) == parseInt($settings.scrolled) ) {
						SUPER.init_popups.show( $this, $settings );
					} 
				}); 
			}

			// Display popup after X seconds  
			if( $settings.enable_seconds=='true' ) {
				 setTimeout(function(){
					SUPER.init_popups.show( $this, $settings );
				 }, parseInt($settings.seconds) * 1000 );
			}

			// Display popup after X seconds of inactivity
			if( parseInt($settings.inactivity) > 0 ) {
				var idleTimer = null;
				var idleState = false;
				var idleWait = parseInt($settings.inactivity) * 1000;
				$('*').bind('mousemove hover dblclick mouseenter mouseleave click mouseup mousedown keydown scroll', function () {
					if(!idleState) {
						clearTimeout(idleTimer);
						idleTimer = setTimeout(function () {
							SUPER.init_popups.show( $this, $settings );
							idleState = true;
							clearTimeout(idleTimer);
						}, idleWait);
					}
				});
				$('body').trigger('mousemove');
			 }

		});
	}

	// Function to show / display the popup
	SUPER.init_popups.show = function( $this, $settings ) {
		if( !$this.hasClass('initialized') ) {
			if( $settings.from != '' && $settings.till != '' ) {
				var from = $settings.from;
				var till = $settings.till;
				var currentDate = new Date();
				from = from.split('-');
				from = from[1]+'/'+from[2]+'/'+from[0];
				till = till.split('-');
				till = till[1]+'/'+till[2]+'/'+till[0];
				currentDate =  (currentDate.getMonth() + 1)+'/'+currentDate.getDate()+'/'+currentDate.getFullYear();
				if(SUPER.init_popups.datecheck(from, till, currentDate)) {
					 SUPER.init_popups.theme_settings( $this, $settings );
					 SUPER.init_popups.slide_in( $this, $settings );
				}
				SUPER.init_super_responsive_form_fields();
			}
		}
	}

	// Compare date with date range
	SUPER.init_popups.datecheck = function( $fromdate, $todate, $checkdate ) {
		var from, to_date, check_date;
		from = Date.parse($fromdate);
		to_date = Date.parse($todate);
		check_date = Date.parse($checkdate);
		if( ( check_date <= to_date && check_date >= from ) ) {
			return true;
		}
		return false;
	}

	// Popup theme settings
	SUPER.init_popups.theme_settings = function( $this, $settings ) {

		// Background color 
		if( $settings.background_color != '' ) {
			$this.css('background-color',$settings.background_color);
		}

		// Background Image
		if( $settings.background_image != '' ) {
			$this.css('background-image','url('+$settings.background_image+')');
		}

		// Background image repeat
		if( $settings.background_image_repeat != '' ) {
			$this.css('background-repeat',$settings.background_image_repeat);
		}

		// Background image size
		if( $settings.background_image_size != '' ) {
			$this.css('background-size',$settings.background_image_size);
		}

		// Border radius  
		var border_radius = $settings.border_radius_top_left+'px ';
		border_radius += $settings.border_radius_top_right+'px '; 
		border_radius += $settings.border_radius_bottom_right+'px ';
		border_radius += $settings.border_radius_bottom_left+'px '; 

		// Set border radius to popup 
		$this.css('border-radius', border_radius);
		$this.css('-moz-border-radius', border_radius);
		$this.css('-webkit-border-radius', border_radius);  

		// Border size in Pixels (px)
		if( $settings.border_size != '' ) {
			// Border color
			var border_color = '';
			if( $settings.border_color != '' ) {
				border_color = $settings.border_color;
			}
			$this.css('border', $settings.border_size+'px'+' solid '+border_color);
		}

		// Background shadow    
		if( $settings.popup_enable_shadows == 'true' ) {

			var box_shadow = $settings.shadow_horizontal_length+'px ';
			box_shadow += $settings.shadow_vertical_length+'px ';
			box_shadow += $settings.blur_radius+'px ';
			box_shadow += $settings.spread_radius+'px '; 
			box_shadow += SUPER.init_popups.hexToRgba( $settings.shadow_color, $settings.shadow_opacity );

			// Set background shadow to popup
			$this.css('-webkit-box-shadow', box_shadow);
			$this.css('-moz-box-shadow', box_shadow);
			$this.css('box-shadow', box_shadow);
		}

		return $this;

	}

	// Convert color code to rgba
	SUPER.init_popups.hexToRgba = function( hex, a ) {
		var bigint, r, g, b;
		var re = /^#?/;
		var aRgb = hex.replace(re, '');
		if (aRgb.length == 3) {
				bigint = parseInt(aRgb, 16);
				r = (bigint >> 4) & 255;
				g = (bigint >> 2) & 255;
				b = bigint & 255;
				return 'rgba(' + r + ',' + g + ',' + b + ',' + a + ')';
		}
		if (aRgb.length == 6) {
				bigint = parseInt(aRgb, 16);
				r = (bigint >> 16) & 255;
				g = (bigint >> 8) & 255;
				b = bigint & 255;
				return 'rgba(' + r + ',' + g + ',' + b + ',' + a + ')';
		}
	}

	// Init Responsive Popup
	SUPER.init_responsive_popup = function($classes) {
	    $('.super-popup-wrapper > .super-popup').each(function(){
	        var $this = $(this);
	        var $width = $(this).outerWidth(true);
	        var $height = $(this).outerHeight(true);
	        var $body_width = $('body').outerWidth(true);
	        var $difference = ($body_width-$width) / 2;
			var $window_height = $( window ).height();
	        if( $window_height > $height ) {
	        	var $difference_height = ($window_height - $height) / 2;
	        }else{
	        	var $difference_height = 30;
	        }
	        if($difference_height<=30){
	        	$difference_height=50;
	        }
	        $this.css('left', $difference+'px');
	        $this.css('top', $difference_height+'px');
	        $this.children('.super-popup-content').css('max-height', ($window_height-70)+'px');
	    });
	}

	// Slide in function
	SUPER.init_popups.slide_in = function( $this, $settings ) {
		if($('body .super-popup-overlay').length==0){
			$('body').prepend('<div class="super-popup-overlay"></div>');
		}
		var overlay_color = '#000000';
		if($settings.overlay_color != ''){
			overlay_color = $settings.overlay_color;
		}
		var overlay_opacity = '#000000';
		if($settings.overlay_opacity != ''){
			overlay_opacity = $settings.overlay_opacity;
		}
		$('.super-popup-overlay').css({ 
			'background': overlay_color,
			'opacity': overlay_opacity,
		});

		var space_from_top = 100;
		var left_position = (($(window).width() - $this.outerWidth()) / 2) + $(window).scrollLeft();
		var right_position = (($(window).width() - $this.outerWidth()) / 2) + $(window).scrollLeft();
		var top_position = ($(window).scrollTop()+space_from_top);
		var bottom_position = ((($(window).height() - $this.outerHeight()) / 2) + $(window).scrollTop())+(space_from_top/3);
		var default_slide_duration = 1000; 

        var $width = $this.outerWidth(true);
        var $height = $this.outerHeight(true);
        var $body_width = $('body').outerWidth(true);
        var $difference = ($body_width-$width) / 2;
		var $window_height = $( window ).height();
        if( $window_height > $height ) {
        	var $difference_height = ($window_height - $height) / 2;
        }else{
        	var $difference_height = 30;
        }
        if($difference_height<=30){
        	$difference_height=50;
        }
        top_position = $difference_height;

		//Popup FadeIn duration in milliseconds (0 is no fade effect)
		if( parseInt($settings.fade_duration) > default_slide_duration ) {
			default_slide_duration = parseInt( $settings.fade_duration );
		}

		// Make popup sticky (stick to top, right, bottom or left) 
		if(  $settings.sticky != 'default' ) {
			var sticky_type =  $settings.sticky; 
			if(  sticky_type == 'top' ){
				top_position = 0;
				$this.hide().css({ 
					'top':  top_position + 'px',
					'left':  left_position + 'px' , 
					'opacity': 1,
				}).fadeIn(parseInt($settings.fade_duration));  
			}
			else if(  sticky_type == 'left' ) {
				left_position = 0;
				$this.hide().css({ 
					'top':  top_position + 'px',
					'left':  left_position + 'px' , 
					'opacity': 1,
				}).fadeIn(parseInt($settings.fade_duration));  
			}
			else if(  sticky_type == 'bottom' ){ 
				 bottom_position = 0;
				 $this.hide().css({  
					'bottom':  bottom_position + 'px', 
					'left':  left_position + 'px' , 
					'opacity': 1,
				 }).fadeIn(parseInt($settings.fade_duration));  
			}  
			else if(  sticky_type == 'right' ) {
				right_position = 0;
				$this.hide().css({ 
					'top':  top_position + 'px',
					'right':  right_position + 'px' , 
					'opacity': 1,
				 }).fadeIn(parseInt($settings.fade_duration));
			}
			$this.addClass('initialized');
			return;
		}

		// Default popup slide show
		if( $settings.slide  == 'default' ) {
			$this.hide().css({ 
				'top':  top_position + 'px',
				'left':  left_position + 'px' , 
				'opacity': 1,
			}).fadeIn(parseInt($settings.fade_duration));  
		}

		// Slide from bottom
		if( $settings.slide  == 'from_bottom' ) {
			$this.css({ 
				'bottom': '0px',
				'left':  left_position + 'px',
			}).animate({
					opacity: 1,
					bottom: bottom_position + 'px' 
			},default_slide_duration); 
		}

		// Slide from right
		if( $settings.slide  == 'from_right' ) {
			$this.css({ 
				'top': top_position + 'px',
				'right':  '0px', 
			}).animate({
					opacity: 1,
					right: right_position + 'px' 
			},default_slide_duration);  
		}

		// Slide from top
		if( $settings.slide  == 'from_top' ) {
			$this.css({ 
				'top': '0px', 
				'left':  left_position + 'px' , 
			}).animate({
					top: top_position + 'px',
					opacity:1,
			},default_slide_duration);  
		}

		// Slide from left
		if( $settings.slide  == 'from_left' ) {
			$this.css({ 
				'top': top_position + 'px',
				'left':  '0px', 
			}).animate({
					opacity: 1,
					left: left_position + 'px' 
			},default_slide_duration);  
		}
		$this.addClass('initialized');
	};

	// Hide popup
	SUPER.init_popups.hide = function() {
		$('.super-popup.initialized').removeClass('initialized');
		$('.super-popup-overlay').remove();
	}

	$(document).ready(function(){
		var $doc = $(document);
		$doc.on('click', '.super-popup-close', function(){
			SUPER.init_popups.hide();
		});
		$(window).click(function(e){
			if(!$(e.target).parents().andSelf().is(self)) {
				SUPER.init_popups.hide();
			}
		});
		$(window).keyup(function(e){
			if(e.keyCode === 27){
				SUPER.init_popups.hide();
			}
		});
	});

	SUPER.init_popups();

})(jQuery);


